    std::cout << "Hello, World!" << std::endl;
    std::cout << "Welcome to C++!" << std::endl;
    return 0;
}

#include <iostream>

int main() {
    int age = 25;              // whole numbers
    float price = 19.99;       // decimal numbers
    double pi = 3.14159;       // more precise decimals
    char grade = 'A';          // single character
    bool isPassed = true;      // true or false

    std::cout << "Age: " << age << std::endl;
    std::cout << "Price: $" << price << std::endl;
    std::cout << "Grade: " << grade << std::endl;
    return 0;
}

#include <iostream>

int main() {
    int age;
    std::string name;

    std::cout << "Enter your name: ";
    std::cin >> name;

    std::cout << "Enter your age: ";
    std::cin >> age;

    std::cout << "Hello " << name << ", you are " << age << " years old!" << std::endl;
    return 0;
}

#include <iostream>

// A simple function that adds two numbers
int add(int a, int b) {
    return a + b;
}

// A function that prints a greeting
void greet() {
    std::cout << "Hello from a function!" << std::endl;
}

int main() {
    greet();  // calling the greet function

    int sum = add(5, 3);  // calling the add function
    std::cout << "5 + 3 = " << sum << std::endl;

    return 0;
}
