#include <iostream>
#include <string>

class Student {
public:
    std::string name;
    int age;

    void introduce() {
        std::cout << "Hi, I'm " << name << " and I'm " << age << " years old." << std::endl;
    }
};

int main() {
    Student student1;
    student1.name = "Alice";
    student1.age = 20;
    student1.introduce();
    return 0;
}
